# data:image/jpeg;base64,

from threading import Thread
from urllib.request import urlopen, HTTPError, URLError



filename = "SampleFile"
# url = "https://file-examples-com.github.io/uploads/2017/11/file_example_MP3_700KB.mp3"
# url = "https://www.seedr.cc/zip/113617988?st=51b07e0ff9b96e9d31f6a74340773df1959fa535727a86818f14cb09fb3af392&e=1615322452"
url = "https://file119.iijj.nl/?%3D"
filesize = ""

def startDownload():
    t = Thread(target=init_download, args=())
    t.start()

def init_download():
    global url
    global filename
    global filesize
    target = urlopen(url)
    meta = target.info()
    filesize = float(meta["Content-Length"])
    print("File size with Method : ", filesize)
    filesize_mb = round((filesize / 1024 / 1024), 2)
    print("File size in MB : ", filesize_mb)
    chunks = 1024 * 5
    downloaded = 0
    with open(filename, "wb") as f:
        while True:
            parts = target.read(chunks)
            if not parts:
                print("File as been downloaded")
                break
            f.write(parts)
            downloaded += chunks
            print(f"\r{round((downloaded / 1024 / 1024), 2)} MB / {filesize_mb} MB", end="")

        f.close()
    print(filesize_mb)

print("Started")
startDownload()